head.script:
function showWData(data){
	var div = UWA.$element(widget.body.getElementsByClassName('wd')[0]);
	if(data.u && data.t){
		var htmlCode = '<img style="margin-bottom:-3px;width:16px;height:16px;" src="'+unescape(data.i)+'" /> <a href="http://www.mynon.cn/files/bk_click.php?id=m_'+data.w+'&uid='+unescape(data.u)+'&f=ow_sohu" target="_blank">'+unescape(data.t)+'</a>';
		div.setHTML(htmlCode);
	}else{
		div.hide();
	}
}
function getWData(wid) {
	var url='http://www.mynon.cn/widget/data/interlink/'+wid+'/json';
	UWA.Data.getJson(url,showWData);
}
widget.onLoad = function(){
	var str = '<div class="pig"><embed pluginspage="http://www.adobe.com/go/getflashplayer" src="http://www.yourminis.com/Dir/GetContainer.api?uri=yourminis/ErnestoQuezada/mini:roaches"  wmode="transparent" width="190" height="190" FlashVars="buildnumber=undefined&uniqueID=id1186691730625&xheight=190&xwidth=190&title=undefined&mininame=undefined&view=minimal&externalURL=http%3A%2F%2Fct%2Eyourminis%2Ecom%2FDir%2FGetMiniSwfPub%2Eapi%3Furi%3Dyourminis%2FErnestoQuezada%2Fmini%3Aroaches%26%5F%5Fversion%3D1186692726&uri=yourminis%2FErnestoQuezada%2Fmini%3Aroaches&accountname=ErnestoQuezada&width=180&height=180&" type="application/x-shockwave-flash" allowScriptAccess="never" allowNetworking="internal"></embed><div class="wd">数据加载中...</div></div>';
	widget.setBody(str);
	var wid=294;
	getWData(wid);
